//echo $cache;

$apiKey = '942aa02ec04144e1a15f33731223dadb';

try{
	$queryTime = -microtime(true);
	$chAccountId = curl_init('http://www.bungie.net/platform/Destiny/SearchDestinyPlayer/'.$_GET['p'].'/'.$_GET['guardian'].'/');
	curl_setopt($chAccountId, CURLOPT_RETURNTRANSFER, true);
	curl_setopt($chAccountId, CURLOPT_HTTPHEADER, array('X-API-Key: '.$apiKey));
	$ceAccountId = curl_exec($chAccountId);
	
	if($ceAccountId===false)
	{
		throw new Exception(curl_error($chAccountId));
	}

	$jsonAccountId = json_decode($ceAccountId);
	if(count($jsonAccountId->Response)==1)
	{
			$accountId = $jsonAccountId->Response[0]->membershipId;
			$chChars = curl_init('http://www.bungie.net/platform/Destiny/'.$_GET['p'].'/Account/'.$accountId.'/Summary/');
			curl_setopt($chChars, CURLOPT_RETURNTRANSFER, true);
			curl_setopt($chChars, CURLOPT_HTTPHEADER, array('X-API-Key: '.$apiKey));
			$ceChars = curl_exec($chChars);
			if($ceChars===false)
			{
				throw new Exception(curl_error($chChars));
			}
			$jsonChars = json_decode($ceChars);
			$charsWithActivitys = array();
			$definitions = array();
			foreach($jsonChars->Response->data->characters as $key => $char)
			{
				$charId = $char->characterBase->characterId;
				$chChar = curl_init('http://www.bungie.net/Platform/Destiny/Stats/ActivityHistory/'.$_GET['p'].'/'.$accountId.'/'.$charId.'/?mode=Raid&definitions=True');
				curl_setopt($chChar, CURLOPT_RETURNTRANSFER, true);
				curl_setopt($chChar, CURLOPT_HTTPHEADER, array('X-API-Key: '.$apiKey));
				$ceChar = curl_exec($chChar);
				if($ceChar===false)
				{
					throw new Exception(curl_error($chChar));
				}
				$jsonChar = json_decode($ceChar);
				$charsWithActivitys[] = array('character'=>$char, 'activities'=>$jsonChar->Response->data->activities);
				foreach($jsonChar->Response->definitions->activities as $defKey => $definition)
				{
					$definitions[$defKey] = $definition;
				} 
			}
			//print_r($jsonChars);
			echo json_encode(array('charsWithActivities'=>$charsWithActivitys, 'activityDefinitions'=>$definitions,'code'=>0,'message'=>"Ok",'queryTime'=>($queryTime+microtime(true))));
	}
	else
	{
		echo json_encode(array('charsWithActivities'=>null, 'activityDefinitions'=>null,'code'=>1,'message'=>"No or multiple Guardians found!",'queryTime'=>($queryTime+microtime(true))));
	}
}
catch (Exception $e)
{
	echo json_encode(array('charsWithActivities'=>null, 'activityDefinitions'=>null,'code'=> -1,'message'=>$e->getMessage(),'queryTime'=>($queryTime+microtime(true))));
}
